import React from 'react';

function ConfirmationView({ orderDetails, navigateTo }) {
  if (!orderDetails) {
    return <p>No order details available.</p>;
  }

  const { items, total, user } = orderDetails;

  return (
    <div>
      <h1>Order Confirmation</h1>
      <h2>Thank you for your purchase, {user.fullName}!</h2>

      <h3>Order Summary</h3>
      <table className="table">
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.quantity}</td>
              <td>${(item.price * item.quantity).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h4>Total: ${total.toFixed(2)}</h4>

      <h3>User Information</h3>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Address:</strong> {user.address1}{user.address2 && `, ${user.address2}`}, {user.city}, {user.state} {user.zip}</p>
      <p><strong>Card:</strong> {user.cardNumber}</p>

      <button className="btn btn-primary mt-3" onClick={() => navigateTo('browse')}>
        Back to Browse
      </button>
    </div>
  );
}

export default ConfirmationView;
