/* src/components/BrowseView.js */

import React, { useState, useContext } from 'react';
import productsData from '../data/products.json';
import ItemCard from './ItemCard';
import { CartContext } from '../context/CartContext'; // Correct Import

function BrowseView({ navigateTo }) {
  const [searchTerm, setSearchTerm] = useState('');
  const { addItem } = useContext(CartContext);

  const filteredProducts = productsData.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h2>Browse Products</h2>
      <input
        type="text"
        placeholder="Search products..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="form-control mb-4"
      />
      <div className="row">
        {filteredProducts.map((product) => (
          <div key={product.id} className="col-md-4">
            <ItemCard product={product} addItem={addItem} />
          </div>
        ))}
      </div>
      <button className="btn btn-primary mt-4" onClick={() => navigateTo('cart')}>
        Checkout
      </button>
    </div>
  );
}

export default BrowseView;
