/* src/components/CartView.js */

import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext'; // Correct Import
import productsData from '../data/products.json';

function CartView({ navigateTo }) {
  const { cart, removeItem, resetCart } = useContext(CartContext);

  const cartItems = Object.keys(cart).map((id) => {
    const product = productsData.find((item) => item.id === parseInt(id));
    return { ...product, quantity: cart[id] };
  });

  const subtotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  const tax = subtotal * 0.07;
  const total = subtotal + tax;

  const handleOrder = (e) => {
    e.preventDefault();
    navigateTo('confirmation', { cart, total });
    resetCart();
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <ul className="list-group mb-4">
            {cartItems.map((item) => (
              <li
                key={item.id}
                className="list-group-item d-flex justify-content-between align-items-center"
              >
                <div>
                  <h5>{item.name}</h5>
                  <p>${item.price.toFixed(2)}</p>
                </div>
                <div>
                  <button
                    className="btn btn-outline-secondary btn-sm me-2"
                    onClick={() => removeItem(item.id)}
                  >
                    −
                  </button>
                  <span>{item.quantity}</span>
                </div>
              </li>
            ))}
          </ul>
          <h4>Subtotal: ${subtotal.toFixed(2)}</h4>
          <h4>Tax (7%): ${tax.toFixed(2)}</h4>
          <h3>Total: ${total.toFixed(2)}</h3>

          {/* Payment Form */}
          <form onSubmit={handleOrder}>
            <h3>Payment Information</h3>
            <div className="mb-3">
              <label htmlFor="cardNumber" className="form-label">
                Credit Card Number
              </label>
              <input type="text" className="form-control" id="cardNumber" required />
            </div>
            <div className="mb-3">
              <label htmlFor="expiryDate" className="form-label">
                Expiry Date
              </label>
              <input type="text" className="form-control" id="expiryDate" required />
            </div>
            <div className="mb-3">
              <label htmlFor="cvv" className="form-label">
                CVV
              </label>
              <input type="text" className="form-control" id="cvv" required />
            </div>
            <button type="button" className="btn btn-secondary me-2" onClick={() => navigateTo('browse')}>
              Return
            </button>
            <button type="submit" className="btn btn-primary">
              Order
            </button>
          </form>
        </>
      )}
    </div>
  );
}

export default CartView;
