/* src/App.js */

import React, { useState } from 'react';
import BrowseView from './components/BrowseView';
import CartView from './components/CartView';
import ConfirmationView from './components/ConfirmationView';
import './App.css';

function App() {
  // State to manage the current view: 'browse', 'cart', or 'confirmation'
  const [currentView, setCurrentView] = useState('browse');

  // State to store order details after checkout
  const [orderDetails, setOrderDetails] = useState(null);

  // Function to navigate between views
  const navigateTo = (view, details = null) => {
    setCurrentView(view);
    if (details) {
      setOrderDetails(details);
    }
  };

  return (
    <div className="container mt-4">
      {currentView === 'browse' && <BrowseView navigateTo={navigateTo} />}
      {currentView === 'cart' && <CartView navigateTo={navigateTo} />}
      {currentView === 'confirmation' && (
        <ConfirmationView orderDetails={orderDetails} navigateTo={navigateTo} />
      )}
    </div>
  );
}

export default App;
